import React from 'react';
import { Container, Row, Col, Image, Form, Button } from 'react-bootstrap';
import emailjs from 'emailjs-com';
function sendEmail(e) {
    e.preventDefault();
  
    emailjs.sendForm('YOUR_SERVICE_ID', 'YOUR_TEMPLATE_ID', e.target, 'YOUR_USER_ID')
      .then((result) => {
          console.log(result.text);
          alert('Email sent successfully!');
      }, (error) => {
          console.log(error.text);
          alert('Something went wrong!');
      });
  }
  function Footer() {
    return (
      <Container fluid className="bg-dark text-white p-4">
        <Row>
          <Col md={4}>
             <h2>أكاديمية نور القرءان</h2>
            <p>لتحفيظ القرءان الكريم بأحكام التجويد</p>
          </Col>
          <Col md={4}>
            <h4>صفحات</h4>
            <ul>
              <li><a href="/">الرئيسية</a></li>
              <li><a href="/about">ماذا عنا</a></li>
              <li><a href="/contact">تواصل معانا</a></li>
            </ul>
          </Col>
          <Col md={4}>
            <h4>Subscribe</h4>
            <p>Enter your email address to receive our latest news and updates.</p>
            <Form onSubmit={sendEmail}>
              <Form.Group controlId="formBasicEmail">
                <Form.Control type="email" name="email" placeholder="Enter email" required />
              </Form.Group>
              <Button variant="primary" type="submit">
                Submit
              </Button>
            </Form>
          </Col>
        </Row>
        <Row className="mt-4">
          <Col md={6}>
            <p>© 2023 Your Company. All rights reserved.</p>
          </Col>
          <Col md={6} className="text-end">
            <Image src="facebook.png" alt="Facebook" width="32" height="32" className="me-2" />
            <Image src="twitter.png" alt="Twitter" width="32" height="32" className="me-2" />
            <Image src="instagram.png" alt="Instagram" width="32" height="32" className="me-2" />
          </Col>
        </Row>
      </Container>
    );
  }
  
  export default Footer;
