import React from 'react';
import Image from 'react-bootstrap/Image';
import Container from 'react-bootstrap/Container';
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';

function ImageGallery() {
  // An array of image URLs

  const images = [
    "image1.jpg",
    "https://play-lh.googleusercontent.com/Dc8IEXGBuc9KSKLbJA9d0cxNGfu8UCsLB7fQu--o8nxoRbtOaFNo1WfT5EpWn2yhWg=w600-h300-pc0xffffff-pd",
    "https://media.gemini.media/img/large/2014/12/23/2014_12_23_21_40_26_84.jpg",
    "https://source.unsplash.com/random/300x200?sig=4",
    "https://source.unsplash.com/random/300x200?sig=5",
    "https://source.unsplash.com/random/300x200?sig=6",
    "https://source.unsplash.com/random/300x200?sig=7",
    "https://source.unsplash.com/random/300x200?sig=8",
    "https://source.unsplash.com/random/300x200?sig=9",
    "https://source.unsplash.com/random/300x200?sig=10",
    "https://source.unsplash.com/random/300x200?sig=11",
    "https://source.unsplash.com/random/300x200?sig=12",
    
    
  ];

  // A function to render each image in a column
  const renderImage = (url, index) => {
    return (
      <Col key={index} xs={6} md={4} lg={3}>
        <Image src={url} thumbnail />
      </Col>
    );
  };

  return (
    <Container className="py-4">
    <Row className="justify-content-center">
        <Col md={8} lg={6}>
          <h1 className="text-center">أراء عملائنا</h1>
          <p className="text-center">نفتخر بأراء عملائنا ونتطلع للأفضل .</p>
        </Col>
      </Row>
      <Row >
        {images.map(renderImage)}
      </Row>
    </Container>
  );
}

export default ImageGallery;
